INSERT INTO `academics` VALUES ('1', '53', '{\"th\":\"แผ่นพับโรคเลปโตสไปโรซิส ( โรคไข้ฉี่หนู )\",\"en\":\"eng\"}', '{\"th\":\"เนื้อหา : ครอบคลุมทั้งสาเหตุของโรค อาการ สาเหตุการติดโรคของคนในชีวิตประจำวัน การป้องกันโรค และการมีส่วนร่วมของชุมชนในการป้องกันและควบคุมโรคเลปโตสไปโรซิส จัดทำในรูปแบบสารคดีเชิงละคร เหมาะสำหรับโรงเรียน หรือชุมชน สามารถนำไปใช้ให้ความรู้ สำหรับประชาชนทั่วไปที่อ่านเนื้อหาก็สามารถนำความรู้ที่ได้ไปใช้ในการ ป้องกันโรคได้\",\"en\":\"eng\"}', '/gcdnew/uploads/academics/computer-law-50.pdf', '/gcdnew/uploads/mediapublics_image/lepto_leaflet_preface.gif', '2010-10-26 04:05:38', '2010-12-20 08:41:34', '12', '7', 'approve', '0', '2010-12-20 15:41:34', '0');
