INSERT INTO `blogposts` VALUES ('11', 'วง SNSD เปิดตัว Star Collections Cards volume 2.5', '<a href=\"http://www.upload.sanookholiday.com/pic.php?name=09111824101109SNSD01.jpg\" target=\"_blank\"><img src=\"http://www.upload.sanookholiday.com/pic/Myoreginal/09111824101109SNSD01.jpg\" alt=\"\" width=\"529\" height=\"418\" /></a><br /> <br /> <br /> <span style=\"color: yellowgreen;\">หลังจากที่มีคอลเลคชั่นที่ 1 และ 2 ในเดือนมกราคมและมิถุนายน 2010 ตอนนี้วง SNSD เปิดตัว Star Collections Cards volume 2.5 ออกมา<br /> <br /> โดยการ์ดคอลเลคชั่นนี้เป็นภาพของสาวๆ เนื่องจากที่ภาพนั้นเล็ก แฟนๆ ตั้งชื่อคอลเลคชั่นนี้ว่า ภาพมินิ<br /> <br /> มินิการ์ดเหล่านี้ประสบความสำเร็จอย่างมาก คอลเลคชั่นแรกมียอดสั่งเกิน  100,000 ชุดในสัปดาห์แรก และในคอลเลคชั่นที่ 2 ที่เป็น Red Devils  ระหว่างช่วง World Cup ได้ยอดจำหน่ายเกิน 500,000 ในสัปดาห์แรก  บางทียอดสั่งสำหรับ 2.5 นี้อาจจะเป็นล้าน?<br /> <br /> โดยที่คอลเลคชั่นนี้จะมีทั้งหมด 7 การ์ดจำหน่ายที่ Gmarket</span><br /> <br /> <a href=\"http://www.upload.sanookholiday.com/pic.php?name=09111825101109SNSD02.jpg\" target=\"_blank\"><img src=\"http://www.upload.sanookholiday.com/pic/Myoreginal/09111825101109SNSD02.jpg\" alt=\"\" width=\"555\" height=\"421\" /></a><br /> <br /> <a href=\"http://www.upload.sanookholiday.com/pic.php?name=09111825101109SNSD03.jpg\" target=\"_blank\"><img src=\"http://www.upload.sanookholiday.com/pic/Myoreginal/09111825101109SNSD03.jpg\" alt=\"\" width=\"551\" height=\"396\" /></a><br /> <br /> <a href=\"http://www.upload.sanookholiday.com/pic.php?name=09111826101109SNSD04.jpg\" target=\"_blank\"><img src=\"http://www.upload.sanookholiday.com/pic/Myoreginal/09111826101109SNSD04.jpg\" alt=\"\" width=\"541\" height=\"421\" /></a><br /> <br /> <a href=\"http://www.upload.sanookholiday.com/pic.php?name=09111826101109SNSD05.jpg\" target=\"_blank\"><img src=\"http://www.upload.sanookholiday.com/pic/Myoreginal/09111826101109SNSD05.jpg\" alt=\"\" width=\"536\" height=\"420\" /></a><br /> <br /> <a href=\"http://www.upload.sanoo