INSERT INTO `informations` VALUES ('46', '4', null, '{\"th\":\"ข้อปฏิบัติ ในการซื้อและบริโภคหอยแครง หอยแมลงภู่ ให้อร่อยและปลอดภัยจากอาการท้องเสีย\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;ผู้บริโภค&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;ข้อปฏิบัติ&lt;/strong&gt;&lt;strong&gt; &amp;nbsp;ในการซื้อและบริโภคหอยแครง &amp;nbsp;หอยแมลงภู่&amp;nbsp; ให้อร่อยและปลอดภัยจากอาการท้องเสีย&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;/p&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;การล้างหอยเป็นเพียงการกำจัดสิ่งปฏิกูลภายนอก เช่น โคลน เศษวัชพืช ควรล้างหอยด้วยน้ำสะอาด &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;ข้อควรปฏิบัติในการล้างหอย&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ไม่ควรหมุนเวียนน้ำล้างหอยที่สกปรกกลับมาใช้ใหม่ &lt;/li&gt;<n/>&lt;li&gt;ต้องระวังไม่ให้น้ำที่ใช้ล้างหอยปนเปื้อนไปยังหอยที่ล้างสะอาดแล้ว&lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;หอยแมลงภู่&amp;nbsp; &lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ต้องล้างด้วยน้ำประปาให้สะอาด 3 ครั้ง &amp;nbsp;ก่อนนึ่งในน้ำเดือดนาน 7 นาที &lt;strong&gt;หรือ&lt;/strong&gt; อบ &lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; นาน 5 นาที&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;หอยแครง&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ต้องล้างด้วยน้ำประปาให้สะอาด 3 ครั้ง ก่อนนำหอยแครงไปลวกในน้ำเดือด &amp;nbsp;1 &amp;ndash; 2 &amp;nbsp;นาที&amp;nbsp; &lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;กรรมวิธี ดังกล่าวเพียงพอต่อการทำลายเชื้อจุลินทรีที่ปนเปื้อนและแบคทีเรียก่อโรค อาหารเป็นพิษที่พบในหอย โดยคุณภาพทางประสาทสัมผัสของหอยแครง หอยแมลงภู่  ด้านสี&amp;nbsp; กลิ่น รส และเนื้อสัมผัสยังเป็นที่ยอมรับของผู้บริโภค&lt;/p&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;การเลือกซื้อหอยแครงลวก และหอยแมลงภู่ที่นึ่งพร้อมบริโภค &amp;nbsp;จากแหล่งต่างๆ&amp;nbsp; เช่น ตลาดสด ร้านอาหาร &amp;nbsp;และห้างสรรพสินค้า &amp;nbsp;&amp;nbsp;อาจมีโอกาสพบเชื้อก่อโรคอาหารเป็นพิษหลงเหลืออยู่&amp;nbsp; &amp;nbsp;เมื่อเทียบกับหอยแครงลวกและหอยแมลงภู่นึ่งทันที &amp;nbsp;&amp;nbsp;&lt;/strong&gt;ดังนั้น!&lt;strong&gt; &amp;nbsp;เพื่อการบริโภคให้อร่อยและปลอดภัย&lt;/strong&gt;&lt;strong&gt;จากอาการท้องเสีย&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;ควรนำมาทำให้สุกใหม่อีกครั้งก่อนบริโภค&lt;/strong&gt;&lt;strong&gt; &lt;br /&gt;&lt;br /&gt; <n/>&lt;hr /&gt;<n/>&lt;br /&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;strong&gt;<n/>&lt;p&gt;&lt;br /&gt;ผู้ปรุง&lt;/p&gt;<n/>&lt;/strong&gt;&lt;strong&gt;ปรุงถูกวิธี&lt;/strong&gt;&lt;strong&gt; &amp;nbsp;ให้อร่อยและปลอดภัยจากอาการท้องเสีย&lt;br /&gt;&lt;/strong&gt;&lt;strong&gt;:&amp;nbsp; หอยแครง &amp;nbsp;หอยแมลงภู่&lt;/strong&gt;&amp;nbsp;<n/>&lt;p&gt;การล้างหอยเป็นเพียงการกำจัดสิ่งปฏิกูลภายนอก เช่น โคลน เศษวัชพืช ควรล้างหอยด้วยน้ำสะอาด &amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;ข้อควรปฏิบัติในการล้างหอย&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ไม่ควรหมุนเวียนน้ำล้างหอยที่สกปรกกลับมาใช้ใหม่ &lt;/li&gt;<n/>&lt;li&gt;ต้องระวังไม่ให้น้ำที่ใช้ล้างหอยปนเปื้อนไปยังหอยที่ล้างสะอาดแล้ว&lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;หอยแมลงภู่&amp;nbsp; &lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ต้องล้างด้วยน้ำประปาให้สะอาด 3 ครั้ง &amp;nbsp;ก่อนนึ่งในน้ำเดือดนาน 7 นาที &lt;strong&gt;หรือ&lt;/strong&gt; อบ &lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; นาน 5 นาที&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;หอยแครง&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/p&gt;<n/>&lt;ul&gt;<n/>&lt;li&gt;ต้องล้างด้วยน้ำประปาให้สะอาด 3 ครั้ง ก่อนนำหอยแครงไปลวกในน้ำเดือด&amp;nbsp; 1 &amp;ndash; 2&amp;nbsp; นาที&amp;nbsp; &lt;/li&gt;<n/>&lt;/ul&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;กรรมวิธี ดังกล่าวเพียงพอต่อการทำลายเชื้อจุลินทรีที่ปนเปื้อนและแบคทีเรียก่อโรค อาหารเป็นพิษที่พบในหอย โดยคุณภาพทางประสาทสัมผัสของหอยแครง หอยแมลงภู่  ด้านสี&amp;nbsp; กลิ่น รส และเนื้อสัมผัสยังเป็นที่ยอมรับของผู้บริโภค&lt;/p&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;&lt;strong&gt;ควรปรุงและจำหน่ายหอยแครงลวก และหอยแมลงภู่ที่นึ่งสุกใหม่ทันที&lt;/strong&gt;&lt;strong&gt; &amp;nbsp;&amp;nbsp;ตามกรรมวิธีข้างต้นหากลวกหรือนึ่งหอยไว้พร้อมบริโภค&lt;/strong&gt;&lt;strong&gt;ทิ้งค้างไว้นาน&lt;/strong&gt;&lt;strong&gt; &lt;/strong&gt;&lt;strong&gt;อาจมีโอกาสพบเชื้อก่อโรคอาหารเป็นพิษหลงเหลืออยู่&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp; เมื่อเทียบกับหอยแครงลวก และหอยแมลงภู่ที่ปรุงสุกใหม่ทันที &lt;/strong&gt;&lt;/p&gt;\",\"en\":\"\"}', '', '0', '2010-12-09 05:03:25', '2010-12-17 02:36:49', '20', '1', '2010-12-09', '0000-00-00', '', 'approve', '', '1', '', '2010-12-17 02:36:49', null);
INSERT INTO `informations` VALUES ('47', '3', null, '{\"th\":\"ประกาศจ้างผลิตและออกอากาศประชาสัมพันธ์วันป้องกันโรคพิษสุนัขบ้าโลก ปี 2552\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"﻿﻿&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;http://thaigcd.ddc.moph.go.th/images/icon/patsadu/pat_icon.jpg&quot; alt=&quot;&quot; /&gt;<n/>&lt;p&gt;&amp;nbsp;&lt;/p&gt;<n/>&lt;p&gt;&lt;span style=&quot;font-size: 9pt; font-family: \'MS Sans Serif\';&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot; lang=&quot;TH&quot;&gt;ด้วยสำนักโรคติดต่อทั่วไป &lt;/span&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;กรมควบคุมโรค จะดำเนินการสอบราคาจ้างผลิตและออกอากาศประชาสัมพันธ์ทางสถานีวิทยุ โครงการรณรงค์วันป้องกันโรคพิษสุนัขบ้าโลก ปี 2552&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;TH&quot;&gt;รายละเอียดปรากฎตามรายละเอียดคุณลักษณะเฉพาะ &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 9pt; font-family: \'MS Sans Serif\';&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;<n/>&lt;p&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span lang=&quot;TH&quot;&gt;ในการนี้สำนักโรคติดต่อทั่วไป &lt;span style=&quot;font-size: 10pt;&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;กรมควบคุมโรค &lt;/span&gt;&lt;/span&gt;ใคร่ขอเรียนเชิญสาธารณชนวิจารณ์ หรือมีความคิดเห็น เป็นลายลักษณ์อักษรผ่านทาง&amp;nbsp;&lt;a href=&quot;http://thaigcd.ddc.moph.go.th/contact.html&quot;&gt;http://thaigcd.ddc.moph.go.th/contact.html&lt;/a&gt;&lt;/span&gt;&lt;/sp