INSERT INTO `laws` VALUES ('9', '11', null, '{\"th\":\"ชื่อโรคติดต่อต้องแจ้งความ\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"&lt;table border=&quot;0&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; width=&quot;100%&quot; bgcolor=&quot;#f0f8ff&quot;&gt;<n/>&lt;tbody&gt;<n/>&lt;tr&gt;<n/>&lt;td&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt;<n/>&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: #4682b4;&quot;&gt;&lt;strong&gt;ประกาศกระทรวงสาธารณสุข&lt;br /&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color: #4682b4;&quot;&gt;&lt;strong&gt;&lt;br /&gt;เรื่อง ชื่อโรคติดต่อต้องแจ้งความ&lt;br /&gt;&lt;/strong&gt;&lt;br /&gt;&lt;/span&gt;&lt;/p&gt;<n/>&lt;/span&gt; &lt;/span&gt;<n/>&lt;p&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;<n/>&lt;/td&gt;<n/>&lt;/tr&gt;<n/>&lt;tr&gt;<n/>&lt;td&gt;<n/>&lt;p&gt;&lt;span style=&quot;color: #4682b4; font-family: Tahoma; font-size: small;&quot;&gt;อาศัยอำนาจตามความใน มาตรา 5 และมาตรา 22 แห่งพระราชบัญญัติโรคติดต่อ พ.ศ. 2523  อันเป็นพระราชบัญญัติที่มีบทบัญญัติบางประการเกี่ยวกับการจำกัดสิทธิและ เสรีภาพของบุคคล ซึ่งมาตรา 29 ประกอบมาตรา 31 มาตรา 35 มาตรา 36 มาตรา 48  และมาตรา 50  ของรัฐธรรมนูญแห่งราชอาณาจักรไทยบัญญัติให้กระทำได้โดยอาศัยอำนาจตามบท บัญญัติแห่งกฎหมาย  รัฐมนตรีว่าการกระทรวงสาธารณสุขจึงให้ปรับปรุงแก้ไขและให้ประกาศกำหนดขึ้น ใหม่ซึ่งชื่อโรคติดต่อต้องแจ้งความ ดังนี้&lt;/span&gt;&lt;/p&gt;<n/>&lt;/td&gt;<n/>&lt;/tr&gt;<n/>&lt;/tbody&gt;<n/>&lt;/table&gt;<n/>&lt;table border=&quot;0&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; width=&quot;100%&quot; bgcolor=&quot;#f0f8ff&quot;&gt;<n/>&lt;tbody&gt;<n/>&lt;tr&gt;<n/>&lt;td width=&quot;75&quot; align=&quot;left&quot; valign=&quot;top&quot;&gt;<n/>&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;<n/>&lt;/td&gt