INSERT INTO `notices` VALUES ('8', '7', null, '{\"th\":\"สอบราคาจ้างประชาสัมพันธ์โครงการประชาสัมพันธ์โรคพิษสุนัขบ้า ปี 2550 ทางสถานีวิทยุ FM จำนวน 1 สถานี จำนวน 3 รายการ\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"&lt;span style=&quot;font-family: MS Sans Serif; font-size: x-small;&quot;&gt; &lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; ด้วย สำนักโรคติดต่อทั่วไป&amp;nbsp; กรมควบคุมโรค  จะดำเนินการสอบราคาจ้างประชาสัมพันธ์โครงการประชาสัมพันธ์ โรคพิษสุนัขบ้า  ปี 2550 ทางสถานีวิทยุ FM จำนวน 1 สถานี จำนวน 3 รายการ  รายละเอียดปรากฎตามรายละเอียดคุณลักษณะเฉพาะ (จำนวน 5 แผ่น) &lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;  ในการนี้สำนักโรคติดต่อทั่วไป กรมควบคุมโรค ใคร่ขอเรียนเชิญสาธารณชนเสนอแนะ  วิจารณ์ หรือมีความคิดเห็น เป็นลายลักษณ์อักษรหรือทางเว็ปไซต์ &lt;/span&gt;&lt;a href=&quot;http://thaigcd.ddc.moph.go.th/&quot;&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt;http://thaigcd.ddc.moph.go.th&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family: Tahoma;&quot;&gt; มายังสำนักโรคติดต่อทั่วไป กรมควบคุมโรค โดยเปิดเผยตัวภายในวันที่ 20 - 22 มีนาคม 2550 เวลา 08.30 - 16.30 น.&lt;/span&gt; &lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp; &lt;br /&gt; &lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbs