INSERT INTO `notices` VALUES ('16', '7', null, '{\"th\":\"สอบราคาจ้างพัฒนาและปรับปรุงระบบการออกเอกสารรับรองการให้วัคซีนหรือการให้ยาป้องกันโรคระหว่างประเทศ (สมุดเล่มเหลือง) จำนวน 1 งาน\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ด้วยสำนักโรคติดต่อทั่วไป&lt;/span&gt;&amp;nbsp; &lt;span lang=&quot;TH&quot;&gt;กรมควบคุมโรค จะดำเนินการสอบราคาจ้างพัฒนาและปรับปรุงระบบการออกเอกสารรับรองการให้วัคซีนหรือการให้ยาป้องกันโรคระหว่างประเทศ &lt;/span&gt;(&lt;span lang=&quot;TH&quot;&gt;สมุดเล่มเหลืองและปรับปรุง&lt;/span&gt;) &lt;span lang=&quot;TH&quot;&gt;จำนวน &lt;/span&gt;1 &lt;span lang=&quot;TH&quot;&gt;งาน รายละเอียดปรากฎตามรายละเอียดคุณลักษณะเฉพาะ&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span lang=&quot;TH&quot;&gt; &lt;/span&gt;&lt;/span&gt; &lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span lang=&quot;TH&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;ในการนี้สำนักโรคติดต่อทั่วไป กรมควบคุมโรค &lt;/span&gt;ใคร่ขอเรียนเชิญสาธารณชนวิจารณ์ หรือมีความคิดเห็น เป็นลายลักษณ์อักษรผ่านทาง&amp;nbsp;&lt;a href=&quot;http://thaigcd.ddc.moph.go.th/contact.html&quot;&gt;http://thaigcd.ddc.moph.go.th/contact.html&lt;/a&gt;&lt;/span&gt; หรือผ่านทาง&lt;span lang=&quot;TH&quot;&gt;งานพัสดุและยานพาหนะ สำนักโรคติดต่อทั่วไป อาคาร 5 ชั้น 5 กรมควบคุมโรค ภายในวันที่ 16 &lt;span lang=&quot;TH&quot;&gt;&amp;nbsp;มิถุนายน &lt;/span&gt;2551 &lt;span lang=&quot;TH&quot;&gt;เวลา &lt;/span&gt;08.30 - 16.30 &lt;span lang=&q