INSERT INTO `notices` VALUES ('28', '7', null, '{\"th\":\"สอบราคาจ้างตรวจยืนยันการตรวจวินิจฉัยพยาธิโปรโตซัว ในคน ในมูลสัตว์รังโรคทุกชนิด ตรวจตัวอ่อนพยาธิฯ จำนวน 5 รายการ\",\"en\":\"\"}', '{\"th\":\"\",\"en\":\"\"}', '{\"th\":\"&lt;span style=&quot;font-size: 9pt; font-family: \'MS Sans Serif\';&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&lt;span lang=&quot;TH&quot;&gt;ด้วยสำนักโรคติดต่อทั่วไป &lt;/span&gt;&lt;span lang=&quot;TH&quot;&gt;กรมควบคุมโรค จะดำเนินการสอบราคา &lt;strong&gt;&lt;span style=&quot;color: #8b0000;&quot;&gt;จ้าง ตรวจยืนยันการตรวจวินิจฉัยพยาธิ โปรโตชัวในคน ในมูลสัตว์รังโรคทุกชนิด  ตรวจตัวอ่อนพยาธิ ในฮสท์กึ่งกลาง ในพื้นที่ของโครงเขื่อนแควน้อย ปี 2552&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;color: #8b0000;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span lang=&quot;TH&quot;&gt;รายละเอียดปรากฎตามรายละเอียดคุณลักษณะเฉพาะ &lt;/span&gt; &lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;font-family: Tahoma; font-size: small;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span lang=&quot;TH&quot;&gt;ในการนี้สำนักโรคติดต่อทั่วไป &lt;span lang=&quot;TH&quot;&gt;กรมควบคุมโรค &lt;/span&gt;ใคร่ขอเรียนเชิญสาธารณชนวิจารณ์ หรือมีความคิดเห็น เป็นลายลักษณ์อักษรผ่านทาง&amp;nbsp;&lt;a href=&quot;http://thaigcd.ddc.moph.go.th/contact.html&quot;&gt;http://thaigcd.ddc.moph.go.th/contact.html&lt;/a&gt;&lt;/span&gt; หรือ&lt;span lang=&quot;TH&quot;&gt;ฝ่าย งานพัสดุและยานพาหนะ สำนักโรคติดต่อทั่วไป อาคาร 5 ชั้น 5 กรมควบคุมโรค  ระหว่างวันที่&amp;nbsp;7&amp;nbsp;มกราคม 2552 ถึงวันที่&amp;nbsp;9&amp;nbsp;มกราคม 2552  หรือสอบถามทางโทรศัพท์ หมายเลข 0 2590 3163 และ 0 2965 9213&lt;/span&gt; &lt;/span&gt;<n/>&lt;blockquote style=&quot;margin-right: 0px;&quot; dir