INSERT INTO `weblinks` VALUES ('00000000055', '28', '{\"th\":\"องค์การเภสัชกรรม\",\"en\":\"\"}', 'http://www.gpo.or.th', '{\"th\":\"     องค์การเภสัชกรรมเป็นองค์กรของรัฐมีภารกิจหลักในการผลิตยาและเวชภัณฑ์ สนับสนุนงานสาธารณสุขของประเทศ นับเป็นโรงงานผลิตยาแผนปัจจุบันแห่งแรกของประเทศไทย ถือกำเนิดขึ้นจากการรวมกิจการของโรงงานเภสัชกรรม ซึ่งก่อตั้งขึ้นตั้งแต่ พ.ศ.2482 กับกองโอสถศาลา ซึ่งก่อตั้งขึ้นในปี พ.ศ.2444 ตามพระราชบัญญัติองค์การเภสัชกรรม พุทธศักราช 2509 มีฐานะเป็นรัฐวิสาหกิจในสังกัดกระทรวงสาธารณสุข\",\"en\":\"\"}', '_blank', '4d06fb1adefd2.jpg', '2010-12-14 05:05:30', '2010-12-14 05:05:30', '21');
